import axios from "axios";

const BASE_URL = "http://localhost:5000/api/";
// const TOKEN =
//   JSON.parse(JSON.parse(localStorage.getItem("persist:root")).user).currentUser
//     .accessToken || "";

// const user = JSON.parse(localStorage.getItem("persist:root"))?.user;
// const currentUser = user && JSON.parse(user).currentUser;
const TOKEN = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjY0NTdkMTcwYzZlYzQ4MjVhZWIwZTQ5MCIsImlzQWRtaW4iOnRydWUsImlhdCI6MTY4MzQ3NzI1NSwiZXhwIjoxNjgzNDc3MzE1fQ.LwH2_blMP_QT81K03FxUNBW3FTkCvVzVNiu53dbCLaE"

export const publicRequest = axios.create({
  baseURL: BASE_URL,
});

export const userRequest = axios.create({
  baseURL: BASE_URL,
  header: { token: `Bearer ${TOKEN}` },
});
